use Kernel::System::ObjectManager;

$Kernel::OM = Kernel::System::ObjectManager->new();

# require our helper
require '_Helper.pl';

# require our common library
require '_StepsLib.pl';

# feature specific steps 

Given qr/a article attachment$/, sub {
   ( S->{Response}, S->{ResponseContent} ) = _Post(
      URL     => S->{API_URL}.'/tickets/'.S->{TicketID}.'/articles/'.S->{ArticleID}.'/attachments',
      Token   => S->{Token},
      Content => {
        Attachment => {
           Content => "", 
           ContentType => "application/pdf",
           Disposition => "attachment",
           Filename => "ein-langer-dateiname-fuer-eine-pdf-datei-ein-langer-dateiname-fuer-eine-pdf-datei.pdf" 
        } 
      }
   );
};
 
When qr/I create a article attachment$/, sub {
   ( S->{Response}, S->{ResponseContent} ) = _Post(
      URL     => S->{API_URL}.'/tickets/'.S->{TicketID}.'/articles/'.S->{ArticleID}.'/attachments',
      Token   => S->{Token},
      Content => {
        Attachment => {
           Content => "", 
           ContentType => "application/pdf", 
           Filename => rand(4)."2ein-langer-dateiname-fuer-eine-pdf-datei-ein-langer-dateiname-fuer-eine-pdf-datei.pdf" 
        } 
      }
   );
};


